package mypackages;


public class IncreamentDecrementOperators {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=6;
		System.out.println(num1++);
		System.out.println(++num1);
		System.out.println(num1--);
		System.out.println(--num1);
		
	}
}
 